import PropTypes from 'prop-types';
import Immutable from 'seamless-immutable';

import { actions } from './actions';
import { matrix } from '../../constants';

const defaultState = {
  moves: 0,
  board: matrix,
  indexGrid: matrix[0].findIndex(elem => elem.active),
  indexRow: 0
};

export function reducer(state = Immutable(defaultState), action) {
  switch (action.type) {
    case 'MOVE_DOWN': {
      // const resetBoard = resetPositionActive();
      const resetBoard = state.board.board;
      const boardUpdated = [
        ...resetBoard.slice(0, indexRow + 1),
        resetBoard[state.board.indexRow + 1][state.board.indexGrid].merge({active: true}),
        // [
        //   ...resetBoard[indexRow + 1].slice(0, IndexGrid),
        //   {
        //     active: true,
        //     enabled: true
        //   },
        //   ...resetBoard[indexRow + 1].slice(IndexGrid + 1 ),
        // ],
        ...resetBoard.slice(indexRow + 2)
      ];
      // setindexRow(indexRow + 10
      return state.merge({ moves: state.moves + 1, board: boardUpdated, indexRow: state.indexRow + 1});
    }
    default: {
      return state;
    }
  }
}

export function propTypes() {
  return {
    board: PropTypes.shape({
      message: PropTypes.string.isRequired,
      type: PropTypes.oneOf(['info', 'error', 'success']).isRequired,
      title: PropTypes.string
    })
  };
}
