import React from 'react';
import { useState, useCallback } from 'react';

import './Modal.css';
import keyboard from './images/keyboard-arrow.png';

function Modal({ children }) {
  const [ visible, setVisible ] = useState(true);

  const closeModal = useCallback(
    () => {
      setVisible(!visible);
    },
    [visible],
  );

    return (
      visible && (
        <div className="modal-container">
          <h1 className="title">Al Rescate</h1>
          <h3 className="text">Gandalf se metio en problemas, se encuentra a unos pocos kilometros de Minas Morgul y Sauron lo tiene en la mira, no le queda mucho tiempo !</h3>
          <h4 className="text">Tienes dos alternativas, la primera es hacer click en el laberinto y guiarlo.</h4>
          <img src={keyboard} className="icon-keyboard"></img>
          <h4 className="text">La segunda es pedirle ayuda a las agilas, ellas vendran y lo sacaran de la mira de Sauron.</h4>
          <div className="container-button-close">
            <button className="button-close" onClick={closeModal}>Cerrar</button>
          </div>
        </div>
      )
    );
}

export default Modal;
