import { stringArrayToObject } from './utils';

export const actions = stringArrayToObject(['ALERT']);

const privateActionCreators = {
  alert(alert, type) {
    return {
      type: actions.ALERT,
      payload: { alert: { ...alert, type } }
    };
  }
};

export const actionCreators = {
  alert(alert) {
    return privateActionCreators.alert({ message: alert.message }, alert.type);
  },
  error(alert) {
    return privateActionCreators.alert(alert, 'error');
  },
  info(alert) {
    return privateActionCreators.alert(alert, 'info');
  },
  success(alert) {
    return privateActionCreators.alert(alert, 'success');
  }
};
