    //       if (grid.active) {
    //         indexActive = gridIndex;
    //       return { ...grid, active: false }
    //     } else {
    //       return grid;
    //     }
    //   })
    // });
    const matrixUpdate = update(1, 1, 1, 2, board);
    debugger;
    setBoard([...board, board[1][2].active = true])
  };

  return (
    <div className="board">
      <button className="help" onClick={handleonClick}>Pidele ayuda a la aguilas</button>
      {board && board.map(row => (
        <div key={row} className="row">
          {row && row.map((grid, i) =>
            <Grid key={i} enabled={grid.enabled} active={grid.active} />
          )}
        </div>
      ))}
    </div>
  );
}

export default Board;
