import React from 'react';
import { useState, useEffect, useCallback } from 'react';
import PropTypes from 'prop-types';
import ArrowKeysReact from 'arrow-keys-react';

import Grid from './components/Grid';
import './Board.css';

function Board({ matrix }) {
  const [ board, setBoard ] = useState(matrix);
  const [ gandalfIndexGrid, setGandalfIndexGrid ] = useState(board[0].findIndex(elem => elem.active));
  const [ gandalfIndexRow, setGandalfIndexRow ] = useState(0);


  useEffect(() => {
    ArrowKeysReact.config({
      left: () => {
        handleLeft();
      },
      right: () => {
        handleRight();
      },
      up: () => {
        handleUp();
      },
      down: () => {
        handleDown();
      }
    });
  });

  const resetPositionActive = useCallback(
    () => {
      const boardUpdated = [
        ...board.slice(0, gandalfIndexRow),
        [
          ...board[gandalfIndexRow].slice(0, gandalfIndexGrid),
          {
            active: false,
            enabled: true
          },
          ...board[gandalfIndexRow].slice(gandalfIndexGrid + 1 ),
        ],
        ...board.slice(gandalfIndexRow + 1)
      ];
      return boardUpdated;
    }
  ,[gandalfIndexRow, gandalfIndexGrid]);

  const handleDown = useCallback(
    () => {
      if (board[gandalfIndexRow + 1][gandalfIndexGrid].enabled) {
        const resetBoard = resetPositionActive();
        const boardUpdated = [
          ...resetBoard.slice(0, gandalfIndexRow + 1),
          [
            ...resetBoard[gandalfIndexRow + 1].slice(0, gandalfIndexGrid),
            {
              active: true,
              enabled: true
            },
            ...resetBoard[gandalfIndexRow + 1].slice(gandalfIndexGrid + 1 ),
          ],
          ...resetBoard.slice(gandalfIndexRow + 2)
        ];
        setGandalfIndexRow(gandalfIndexRow + 1);
        setBoard(boardUpdated);
      }
    }
  );

  const handleRight = useCallback(
    () => {
      if (board[gandalfIndexRow][gandalfIndexGrid + 1].enabled) {
        const resetBoard = resetPositionActive();
        const boardUpdated = [
          ...resetBoard.slice(0, gandalfIndexRow),
          [
            ...resetBoard[gandalfIndexRow].slice(0, gandalfIndexGrid + 1),
            {
              active: true,
              enabled: true
            },
            ...resetBoard[gandalfIndexRow].slice(gandalfIndexGrid + 2),
          ],
          ...resetBoard.slice(gandalfIndexRow + 1)
        ];
        setGandalfIndexGrid(gandalfIndexGrid + 1);
        setGandalfIndexRow(gandalfIndexRow);
        setBoard(boardUpdated);
      }
    }
  );

  const handleUp = useCallback(
    () => {
      if (board[gandalfIndexRow - 1][gandalfIndexGrid].enabled) {
        const resetBoard = resetPositionActive();
        const boardUpdated = [
          ...resetBoard.slice(0, gandalfIndexRow - 1),
          [
            ...resetBoard[gandalfIndexRow - 1].slice(0, gandalfIndexGrid),
            {
              active: true,
              enabled: true
            },
            ...resetBoard[gandalfIndexRow - 1].slice(gandalfIndexGrid + 1),
          ],
          ...resetBoard.slice(gandalfIndexRow)
        ];
        setGandalfIndexRow(gandalfIndexRow - 1);
        setBoard(boardUpdated);
      }
    }
  );

  const handleLeft = useCallback(
    () => {
      if (gandalfIndexGrid !== 0 && board[gandalfIndexRow][gandalfIndexGrid - 1].enabled) {
        const resetBoard = resetPositionActive();
        const boardUpdated = [
          ...resetBoard.slice(0, gandalfIndexRow),
          [
            ...resetBoard[gandalfIndexRow].slice(0, gandalfIndexGrid - 1),
            {
              active: true,
              enabled: true
            },
            ...resetBoard[gandalfIndexRow].slice(gandalfIndexGrid),
          ],
          ...resetBoard.slice(gandalfIndexRow + 1)
        ];
        setGandalfIndexGrid(gandalfIndexGrid - 1);
        setGandalfIndexRow(gandalfIndexRow);
        setBoard(boardUpdated);
      }
    }
  );


  return (
    <div className="board" {...ArrowKeysReact.events} tabIndex="1">
      <button className="help" onClick={handleDown}>Pidele ayuda a la aguilas</button>
      {board && board.map(row => (
        <div key={row} className="row">
          {row && row.map((grid, i) =>
            <Grid key={i} enabled={grid.enabled} active={grid.active} />
          )}
        </div>
      ))}
    </div>
  );
}

Board.propTypes = {
  matrix: PropTypes.arrayOf(
    PropTypes.shape({ enabled: PropTypes.bool, active: PropTypes.bool })
  )
};

export default Board;
