import React from 'react';
import { useState, useEffect, useCallback } from 'react';
import PropTypes from 'prop-types';
import ArrowKeysReact from 'arrow-keys-react';

import Grid from './components/Grid';
import './Board.css';

function Board({ matrix }) {
  const [ board, setBoard ] = useState(matrix);
  const [ gandalfIndexGrid, setGandalfIndexGrid ] = useState(board[0].findIndex(elem => elem.active));
  const [ gandalfIndexRow, setGandalfIndexRow ] = useState(0);


  useEffect(() => {
    ArrowKeysReact.config({
      left: () => {
        console.log('left key detected.');
      },
      right: () => {
        console.log('right key detected.');
      },
      up: () => {
        console.log('up key detected.');
      },
      down: () => {
        this.handleDown();
      }
    });
  }, []);

  const handleDown = useCallback(
    () => {
      const matrixUpdated = [
        ...board.slice(gandalfIndexRow, gandalfIndexRow + 1),
        [
          ...board[gandalfIndexRow + 1].slice(gandalfIndexGrid, gandalfIndexGrid + 1 ),
          {
            active: true,
            enabled: true
          },
          ...board[gandalfIndexRow + 1].slice(gandalfIndexGrid + 1 ),
        ],
        ...board.slice(gandalfIndexRow + 2)
      ];
      console.log(matrixUpdated);
      setGandalfIndexRow(1);
      setBoard(matrixUpdated);
    },
    [a, b],
  );

  return (
    <div className="board" {...ArrowKeysReact.events} tabIndex="1">
      <button className="help">Pidele ayuda a la aguilas</button>
      {board && board.map(row => (
        <div key={row} className="row">
          {row && row.map((grid, i) =>
            <Grid key={i} enabled={grid.enabled} active={grid.active} />
          )}
        </div>
      ))}
    </div>
  );
}

Board.propTypes = {
  matrix: PropTypes.arrayOf(
    PropTypes.shape({ enabled: PropTypes.bool, active: PropTypes.bool })
  )
};

export default Board;
