import PropTypes from 'prop-types';
import Immutable from 'seamless-immutable';

import { actions } from './actions';
import matrix from '../../constants';

const defaultState = {
  moves: 0,
  board: matrix,
  indexGrid: matrix[0].findIndex(elem => elem.active),
  indexRow: 0
};

const [ gandalfIndexGrid, setGandalfIndexGrid ] = useState(board[0].findIndex(elem => elem.active));
const [ gandalfIndexRow, setGandalfIndexRow ] = useState(0);

export function reducer(state = Immutable(defaultState), action) {
  switch (action.type) {
    case actions.MOVE: {
      return state.merge({ moves: state.moves + 1});
    }
    default: {
      return state;
    }
  }
}

export function propTypes() {
  return {
    board: PropTypes.shape({
      message: PropTypes.string.isRequired,
      type: PropTypes.oneOf(['info', 'error', 'success']).isRequired,
      title: PropTypes.string
    })
  };
}
