import React from 'react';
import { useState } from 'react';

import Grid from './components/Grid';
import './Board.css';

function Board({ matrix }) {
  const [ board, setBoard ] = useState(matrix);
  const handleonClick = () => {
    const matrixUpdate = board && board.map((row, i) => row.active ? { ...row, active: false } : row)
    setBoard(matrixUpdate)
  }
  return (
    <div className="board">
      <button className="help" onClick={handleonClick}>Pidele ayuda a la aguilas</button>
      {board && board.map(row => (
        <div className="row">
          {row && row.map(grid =>
            <Grid enabled={grid.enabled} active={grid.active} />
          )}
        </div>
      ))}
    </div>
  );
}

export default Board;
