import React from 'react';
import { useState } from 'react';
import PropTypes from 'prop-types';

import Grid from './components/Grid';
import { update } from './utils';
import './Board.css';

function Board({ matrix }) {
  const [ board, setBoard ] = useState(matrix);
  const handleonClick = () => {
    let indexRowActive = board && board[0].findIndex(row => row.active);
    let indexBoardActive = 0;
    const newFields = [
      ...board.slice(0, 1),
      board[1].merge({
        [nameField]: valueField,
        ebanled:true
      }),
      ...reportArray.slice(1 + 1)
    ];
    const matrixUpdate = board &&
      board.map((row, boardIndex) => {
        return row.map((grid, gridIndex) => {
          if (grid.active) {
            indexRowActive = gridIndex;
            indexBoardActive = boardIndex;
            return { ...grid, active: false }
          } else if (grid.enabled) {
            if (indexRowActive === gridIndex && boardIndex - indexBoardActive === 1) {
              return { ...grid, active: true }
            } else if (gridIndex - indexRowActive === 1 && boardIndex === indexBoardActive) {
              return { ...grid, active: true }
            } else {
              return grid;
            }
          } else {
            return grid;
          }
        })
      });
    console.log(matrixUpdate)
    setBoard(matrixUpdate)
  };

  return (
    <div className="board">
      <button className="help" onClick={handleonClick}>Pidele ayuda a la aguilas</button>
      {board && board.map(row => (
        <div key={row} className="row">
          {row && row.map((grid, i) =>
            <Grid key={i} enabled={grid.enabled} active={grid.active} />
          )}
        </div>
      ))}
    </div>
  );
}

Board.propTypes = {
  matrix: PropTypes.arrayOf(
    PropTypes.shape({ enabled: PropTypes.bool, active: PropTypes.bool })
  )
};

export default Board;
